import React from "react";
import { Container, Row, Col, Button, ButtonGroup } from "react-bootstrap";
import Icon from "@mdi/react";
import { mdiChevronRight } from '@mdi/js'; 


export function TopBanner(){
    return(
        <Container fluid>
            <Row className="vh-100 justify-content-center align-content-center">
                <Col xs md="4">
                    <h1 className="mb-4">Financia el sueño<br/>de tu empresa</h1>
                    <h6 className="mb-4">
                        Nuestro modelo de riesgos evalúa en segundos una operación,<br/>lo que nos permite financiar en el mismo día.  
                    </h6>
                    
                    <ButtonGroup className="me-2" aria-label="Second group" size="lg">
                        <Button>Empieza a financiarte</Button> 
                        <Button> 
                            <Icon path={mdiChevronRight}
                            size={1}
                            horizontal
                            vertical
                            rotate={180}
                            color="white"
                            className="iconBanner"/>
                            </Button> 
                    </ButtonGroup>
                </Col>
                <Col lg="5">
                </Col>
            </Row>
        </Container>
    )
}
