import React, { useEffect, useState } from "react";
import { Row, Col, Card, Button } from "react-bootstrap";
import Cards from "./Cards";
import { mdiChevronRight } from '@mdi/js'; 
import Icon from "@mdi/react";

export default function CardsContainer(){
    const [cards, setCards ] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8000/cards')
        .then(res => res.json())
        .then(data => setCards(data))
    }, [])


    return (
        <Row className='my-5 justify-content-center'>   
            <Cards/>

            {cards.map(card => (
        <Col sm={10} md={3}>
        <Card className="text-center">
       <Card.Img variant="top" src="holder.js/100px180" className="image-card"/>
       <Card.Body className="p-5">   
           <Card.Title className="shadow-txt black-txt">     
           {card.title}     
           </Card.Title>
           <Card.Text className="shadow-txt black-txt">
           Pensado en empresas que necesiten financiar sus cuentas por cobrar o una alternativa al factoring. 
           </Card.Text>
           <Button variant="link">Explorar  
           <Icon path={mdiChevronRight}
                       size={1}
                       horizontal
                       vertical
                       rotate={180}
                       />
           </Button>
       </Card.Body>
       </Card>
   </Col>
            ))}
        </Row>
    )
    
}
