import React from "react";
import { Container, Row, Col, Button, ButtonGroup } from "react-bootstrap";
import Icon from "@mdi/react";
import { mdiChevronRight } from '@mdi/js'; 
import { TopNav } from "./TopNav";
import circleGradient from '../img/circleGradient.svg';


export function TopBanner(){
    return(
        <Container fluid className="bg-black g-0 m-0">
            <Row className="position-absolute">
                <Col>
                <img src={circleGradient} />
                </Col>
            </Row>
           
            <TopNav/>     
            <Row className="vh-100 justify-content-center align-content-center">
                <Col xs md="4">
                    <h1 className="mb-4 text-white">Financia el sueño<br/>de tu empresa</h1>
                    <h6 className="mb-4 text-white">
                        Nuestro modelo de riesgos evalúa en segundos una operación,<br/>lo que nos permite financiar en el mismo día.  
                    </h6>
                    
                    <ButtonGroup className="me-2" aria-label="Second group" size="lg">
                        <Button>Empieza a financiarte                             
                            <Icon path={mdiChevronRight}
                            size={1}
                            color="white"
                            
                           />
                        </Button> 

                    </ButtonGroup>
                </Col>
                <Col lg="5">
                </Col>
            </Row>
        </Container>
    )
}
